import React from 'react';
import './EditeProfile';
import CloseIcon from '@mui/icons-material/Close';
import { Box, IconButton, Modal, TextField } from '@mui/material';

const style = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: 600,
    height: 600,
    bgcolor: 'background.paper',
    boxShadow: 24,
    borderRadius: 8,
}
export default function EditeProfile({ user, loggedInUser }) {
    const [open, setOpen] = React.useState(false);
    const [name, setName] = React.useState('');
    const [bio, setBio] = React.useState('');
    const [location, setLocation] = React.useState('');
    const [website, setWebsite] = React.useState('');
    const [dob, setDob] = React.useState('');

    const HandleSave = () => {
        console.log('saving')
        setOpen(false)
    }

    return (
        <div>
            <button className='Edit-profile-btn' onClick={() => setOpen(true)}>Edit Profile</button>
            <Modal
                open={open}
                aria-labelledby="modal-modal-title"
                aria-describedby="modal-modal-description"
            >
                <Box sx={style} className='modal'>
                    <div className='header'>
                        <IconButton onClick={() => { setOpen(false) }}> <CloseIcon /> </IconButton>
                        <h2 className='header-title'>Edit Profile</h2>
                        <button className='save-Btn' onClick={HandleSave} > Save </button>
                    </div>

                    <div className='fill-contant'>
                        <TextField className='text-field' fullWidth is='fullwidth' label='Name' variant='filled'
                        onChange={(e)=>{setName(e.target.value)}} defaultValue={loggedInUser[0]?.name ? loggedInUser[0]?.name :'' } />
                    </div>
                    <div className='birthdate-section'></div>
                    <div className='last-section'></div>

                </Box>
            </Modal>
        </div>
    );
};
