import React, { useEffect, useState } from "react";
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import CenterFocusWeakIcon from '@mui/icons-material/CenterFocusWeak';
import MyLocationIcon from '@mui/icons-material/MyLocation';
import AddLinkIcon from '@mui/icons-material/AddLink';
import LockResetIcon from '@mui/icons-material/LockReset';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import Post from '../../Feed/post/Post';
import UseLoggedInUser from '../../../hooks/useLoggedInUser';
import './MainPage.css';
import EditProfile from "../EditeProfile/EditProfile";

const MainPage = ({ user }) => {
    const navigate = useNavigate();
    const [loggedInUser] = UseLoggedInUser();
    const [isLoading, setIsLoading] = useState('');
    const [posts, setPosts] = useState([]);

    useEffect(() => {
        axios.get(`https://api.imgbb.com/1/upload?key=dcc928c479eeb16dde5c97efcbbe2692`, formData)
        fetch(`http://localhost:5000/userPost?email= ${user?.email}`)
            .then(res => res.json())
            .then(data => {
                setPosts(data);
            })
    }, [posts])

    const username = user?.email?.split('@')[0];

    const handleUploadCoverImage = (e) => {
        setIsLoading(true);
        const image = e.target.files[0];

        const formData = new FormData();
        formData.set('image', image);

        axios.post(`https://api.imgbb.com/1/upload?key=dcc928c479eeb16dde5c97efcbbe2692`, formData)
            .then(res => {
                const url = res.data.data.display_url;
                const userCoverImage = {
                    email: user?.email,
                    coverImage: url
                }
                setIsLoading(false);
                if (url) {
                    axios.patch(`http://localhost:5000/userUpdates/${user?.email}`, userCoverImage)
                }
            })
    }
    const handleUploadProfileImage = (e) => {
        setIsLoading(true);
        const image = e.target.files[0];

        const formData = new FormData();
        formData.set('image', image);

        axios.post(`https://api.imgbb.com/1/upload?key=dcc928c479eeb16dde5c97efcbbe2692`, formData)
            .then(res => {
                const url = res.data.data.display_url;
                const userProfileImage = {
                    email: user?.email,
                    profileImage: url
                }
                setIsLoading(false);
                if (url) {
                    axios.patch(`http://localhost:5000/userUpdates/${user?.email}`, userProfileImage)
                }
            })
    }

    return (
        <div>
            <ArrowBackIcon className='arrow-icon' onClick={() => { navigate('/') }} />
            <h4 className='heading-4'>@{username}</h4>
            <div className='mainProfile'>
                <div className='profile_bio'>
                    {
                        <div>
                            <div className='coverImageContainer'>
                                <img src={loggedInUser[0]?.coverImage ? loggedInUser[0]?.coverImage :
                                    'https://cdn.pixabay.com/photo/2016/08/08/09/17/avatar-1577909_960_720.png'}
                                    alt='' className='coverImage' />
                                <div className='hoverCoverImage'>
                                    <label htmlFor='image' className='imageIcon'>
                                        {
                                            isLoading ?
                                                <LockResetIcon className="photoIcon photoIconDisable" />
                                                :
                                                <CenterFocusWeakIcon className='photoIcon' />
                                        }
                                    </label>
                                    <div className='imageIcon_tweetButton'>
                                        <input type='file' id='image' className='imageInput'
                                            onChange={handleUploadCoverImage} />
                                    </div>
                                </div>
                            </div>
                            <div className='avatar-image'>
                                <div className='avatarContainer'>
                                    <img src={loggedInUser[0]?.profileImage ? loggedInUser[0]?.profileImage :
                                        'https://cdn.pixabay.com/photo/2016/08/08/09/17/avatar-1577909_960_720.png'}
                                        alt='' className='avatar' />
                                    <div className='hoverAvatarImage'>
                                        <div className='imageIcon_tweetButton'>
                                            <label htmlFor='profileImage' className='imageIcon'>
                                                {
                                                    isLoading ?
                                                        <LockResetIcon className="photoIcon photoIconDisable" />
                                                        :
                                                        <CenterFocusWeakIcon className='photoIcon' />
                                                }
                                            </label>
                                            <div className='imageIcon_tweetButton'>
                                                <input type='file' id='profileImage' className='imageInput'
                                                    onChange={handleUploadProfileImage} />
                                            </div>
                                        </div>
                                    </div>
                                </div>
                                <div className='user_Info'>
                                    <div>
                                        <h3 className='heading-3'>
                                            {loggedInUser[0]?.name ? loggedInUser[0]?.name : user && user?.displayName}
                                        </h3>
                                        <p className='usernameSection' >@{username}</p>
                                    </div>
                                    <EditProfile user={user} loggedInUser={loggedInUser} />
                                </div>
                                <div className='infoContainer'>
                                    {loggedInUser[0]?.bio ? loggedInUser[0]?.bio : ''}
                                    <div className='locationAndLink'>
                                        {loggedInUser[0]?.location ? <p className='subInfo'><MyLocationIcon />{loggedInUser[0]?.location}</p> : ''}
                                        {loggedInUser[0]?.website ? <p className='subInfo link'><AddLinkIcon />{loggedInUser[0]?.website}</p> : ''}
                                    </div>
                                </div>
                                <h4 className='tweetsText'>Tweets</h4>
                                <hr />
                            </div>
                            {
                                posts.map(p => <Post id={p._id} p={p} />)
                            }
                        </div>
                    }

                </div>
            </div>
        </div>
    );
}

export default MainPage;
